mapper.select:
select id, account, name, pwd, email, phone, status, note, ctrate_time, update_time
from t_user

    account LIKE #{account,jdbcType=VARCHAR}
    AND name LIKE #{name,jdbcType=VARCHAR}
    AND status = #{status,jdbcType=INTEGER}

select id, account, name, pwd, email, phone, status, note, ctrate_time, update_time
from t_user
where id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE t_user
account = #{account,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, pwd = #{pwd,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, note = #{note,jdbcType=VARCHAR}, update_time = now()
WHERE id = #{id,jdbcType=INTEGER}
