mapper.update:
UPDATE sys_depart

    update_time = now()
    ,name = #{name,jdbcType=VARCHAR}
    ,note = #{note,jdbcType=VARCHAR}
    ,update_user = #{updateUser,jdbcType=VARCHAR}

WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, note, update_user as updateUser, update_time as updateTime
from sys_depart

    id = #{id,jdbcType=INTEGER}
    AND name LIKE #{name,jdbcType=VARCHAR}

select id , name, note, update_user as updateUser, update_time as updateTime from sys_role_depart srd, sys_depart sd where srd.depart_id = sd.id
and srd.role_id in
#{item}

select id, name, note, update_user as updateUser, update_time as updateTime
from sys_depart
where id = #{id,jdbcType=INTEGER}
