mapper.select:
select distinct sm.id,
sm.parent_id as parentId,
sm.name as name,
sm.icon as icon,
sm.url as url,
sm.type as type,
sm.order,
sm.status as status,
sm.update_user as updateUser,
sm.update_time as updateTime
from sys_menu sm,

    name LIKE #{name,jdbcType=VARCHAR}
    AND parent_id = #{parentId,jdbcType=INTEGER}
     AND type = #{type,jdbcType=INTEGER}
    update_user = #{updateUser,jdbcType=VARCHAR}

mapper.update:
UPDATE sys_menu
parent_id = #{parentId,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, icon = #{icon,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR}, order = #{order,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, update_user = #{updateUser,jdbcType=VARCHAR}, update_time = now()
where id = #{id,jdbcType=INTEGER}

mapper.select:
select order
FROM sys_menu

    AND parent_id = #{parentId,jdbcType=INTEGER}
