mapper.select:
SELECT id, name, status, type, create_time, create_user,update_time,update_user
FROM t_role

    name LIKE #{name,jdbcType=VARCHAR}
    AND status = #{status,jdbcType=VARCHAR}

SELECT id, name, status, type, create_time, create_user,update_time,update_user
FROM t_role
WHERE id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE t_role
name = #{name,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR}, update_time = now()
WHERE id = #{id,jdbcType=INTEGER}
