mapper.delete:
DELETE FROM sys_role

    ID = #{id,jdbcType=INTEGER}
    AND depart_id = #{departId,jdbcType=INTEGER}

mapper.select:
SELECT sr.id, sr.name, sd.id as departId,sd.name as departName,sr.note, sr.update_time,sr.update_user
FROM sys_role sr
left join sys_depart sd on sr.depart_id = sd.id

    sr.name LIKE #{name,jdbcType=VARCHAR}
    AND sd.id = #{departId,jdbcType=VARCHAR}

mapper.update:
UPDATE sys_role
name = #{name,jdbcType=VARCHAR}, depart_id = #{departId,jdbcType=INTEGER}, note = #{note,jdbcType=VARCHAR}, update_user = #{updateUser,jdbcType=VARCHAR}, update_time = now()
WHERE id = #{id,jdbcType=INTEGER}
