mapper.delete:
DELETE sys_role_menu
FROM sys_role_menu,sys_menu
WHERE sys_role_menu.menu_id = sys_menu.id
AND sys_menu.parent_id != 0
AND sys_role_menu.menu_id = #{menuId,jdbcType=INTEGER}
AND sys_role_menu.role_id = #{roleId,jdbcType=INTEGER}
AND sys_menu.type = #{menuType,jdbcType=INTEGER}

mapper.select:
SELECT menu_id as id
FROM sys_role_menu

    role_id = #{roleId,jdbcType=INTEGER}
    type = #{menuType,jdbcType=INTEGER}
