mapper.update:
UPDATE sys_user

    update_time = now()
    ,user_name = #{userName,jdbcType=VARCHAR}
    ,password = #{password,jdbcType=VARCHAR}
    ,phone = #{phone,jdbcType=VARCHAR}
    ,sex = #{sex,jdbcType=VARCHAR}
    ,email = #{email,jdbcType=VARCHAR}
    ,note = #{note,jdbcType=VARCHAR}
    ,rank = #{rank,jdbcType=VARCHAR}
    ,question = #{question,jdbcType=VARCHAR}
    ,answer = #{answer,jdbcType=VARCHAR}
    ,status = #{status,jdbcType=INTEGER}
    ,update_user = #{updateUser,jdbcType=VARCHAR}

WHERE user_id = #{userId,jdbcType=VARCHAR}

UPDATE sys_user
SET password = #{password,jdbcType=VARCHAR}, update_time = now()
WHERE user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
SELECT DISTINCT su.user_id, su.user_name, su.password, su.phone, su.sex, su.email, su.note,
su.rank, su.question, su.answer, su.status, su.update_user, su.update_time
FROM sys_user su

, sys_user_role sur, sys_role_depart srd ,sys_depart sd


    su.user_id LIKE #{userId,jdbcType=VARCHAR}
    OR user_name LIKE #{userId,jdbcType=VARCHAR}

        AND sur.user_id = su.user_id
        AND sur.role_id = srd.role_id
        AND srd.depart_id = sd.id

    AND status = #{status,jdbcType=VARCHAR}
